%pyspark
import prov.model as prov
import re
from collections import defaultdict

class QueryResponseProvenance(object):
    PREFIX = re.compile ('^prefix ', re.I)
    """ Abstract model of the provenance of components of a complex query. """
    def __init__(self, default_ns, namespaces=[]):
        """ Specify a default namespace and associated sub namespaces """
        self.document = prov.ProvDocument ()
        self.default_ns = default_ns
        self.document.set_default_namespace (self.default_ns)
        self.namespaces = namespaces
        self.subspaces = {}
        for namespace in self.namespaces:
            self.subspaces[namespace] = self.add_namespace (self.default_ns, namespace)
    def add_namespace (self, root, qualifier):
        subspace = "{0}{1}".format (root, qualifier)
        self.document.add_namespace (qualifier, subspace)
        return subspace
    def add_entity (self, name, tuples):
        self.document.entity (name, tuples)
    def add_data_source (self, name, entity):
        print 
        print (entity.to_tuple())
        self.entity (name, entity.to_tuple ())

class AbstractEntity(object):
    def __init__(self, type, namespace):
        self.attributes = []
        self.namespace = namespace
        self.add_attribute (prov.PROV_TYPE, type)
    def add_attribute (self, iri, value):
        self.attributes.append (('{0}{1}'.format (self.namespace, iri), value))
    def to_tuple (self):
        return tuple(self.attributes)

class DataSource(AbstractEntity):
    def __init__(self, namespace):
        AbstractEntity.__init__(self, type, namespace)
        self.add_attribute ()

''' DataLake Support '''
class DataLakeProvenance(QueryResponseProvenance):
    def __init__(self, default_ns, namespaces=[]):
        QueryResponseProvenance.__init__(self, default_ns, namespaces)

    def parse_sparql (self, query, source_map):
        sources = {}
        for line in query.split ('\n'):
            line = ' '.join (line.strip ().split ())
            print (line)
            print ('  ---- {0} ----'.format (line))
            match = self.PREFIX.match (line)
            if match is None:
                continue
            if True:
                parts = line.strip().split (' ')
                if len(parts) >= 3:
                    iri = parts[2].strip ()
                    print ("iri--> {}".format (iri))
                    e = None
                    for k, v in source_map.iteritems ():
                        if k in iri:
                            if k in sources:
                                e = sources[k]
                            else:
                                e = AbstractEntity ('datasource', '{0}{1}'.format (self.default_ns, k))
                                sources[k] = e
                            e.add_attribute ('datasource', iri)
                    if e is None:
                        e = AbstractEntity ('DataSource', 'http://purl.datasource.org/')
                        e.add_attribute ('datasource', iri)
                    self.add_entity ('datasource', e)

''' Domain Specific Data Sources '''
class MedchemEntity(AbstractEntity):
    def __init__(self, type, namespace):
        AbstractEntity.__init__(self, type, namespace)

class C2B2REntity(MedchemEntity):
    def __init__(self, namespace):
        MedchemEntity.__init__(self, 'DataSource', )
        self.add_attribute ()

