        AbstractEntity.__init__(self, type, namespace)

class C2B2REntity(MedchemEntity):
    def __init__(self, type, ):
        MedchemEntity.__init__(self, type)
        self.source_map = {
            'http://chem2bio2rdf.org/ctd' : 'ctd',
            'http://chem2bio2rdf.org/drugbank' : 'drugbank'
        }
    def parse_query (self, query):
        for k, v in self.source_map.iteritems ():
            if k in query:
                self.add_attribute ('medchem', v)

